const newEngine = require('..').newEngine;

const myEngine = newEngine();

myEngine.query('SELECT * { ?s ?p ?o } LIMIT 100',
  { sources: [ { type: 'hypermedia', value: 'http://fragments.dbpedia.org/' } ] })
  .then(function (result) {
    result.bindingsStream.on('data', console.log);
    result.bindingsStream.on('error', () => console.error('a'));
  }).catch(() => console.error('b'));
